import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ListaClientes from './components/ListaClientes';
import FormularioCliente from './components/FormularioCliente';
import DetalhesCliente from './components/DetalhesCliente';
import EditarCliente from './components/EditarCliente';
import TodosOrcamentos from './components/TodosOrcamentos';
import CriarOrcamento from './components/CriarOrcamento';
import OrcamentosCliente from './components/OrcamentosCliente';

function App() {
  const [clientes, setClientes] = useState([]);

  // Função para adicionar um cliente
  const handleAdicionarCliente = async (cliente) => {
    try {
      const response = await fetch('http://localhost:3000/clientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(cliente)
      });
      if (!response.ok) throw new Error('Erro ao adicionar cliente');
      const novoCliente = await response.json();
      setClientes([...clientes, novoCliente]); // Atualiza a lista de clientes
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao adicionar cliente');
    }
  };

  return (
    <Router>
      <Routes>
        {/* Rotas para clientes */}
        <Route path="/" element={<ListaClientes clientes={clientes} />} />
        <Route path="/adicionar" element={<FormularioCliente onAdicionarCliente={handleAdicionarCliente} />} />
        <Route path="/detalhes/:id" element={<DetalhesCliente />} />
        <Route path="/editar/:id" element={<EditarCliente />} />

        {/* Rotas para orçamentos */}
        <Route path="/orcamentos" element={<TodosOrcamentos />} />
        <Route path="/criar-orcamento" element={<CriarOrcamento />} />
        <Route path="/clientes/:id/orcamentos" element={<OrcamentosCliente />} />
      </Routes>
    </Router>
  );
}

export default App;
