import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function DetalhesCliente() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [cliente, setCliente] = useState({ nome: '', numero: '' });

    // Simulação de requisição ao backend
    useEffect(() => {
        const clientesFake = [
            { id: 1, nome: 'Cliente 1', numero: '123456789' },
            { id: 2, nome: 'Cliente 2', numero: '987654321' }
        ];
        const clienteEncontrado = clientesFake.find(c => c.id === parseInt(id));
        if (clienteEncontrado) {
            setCliente(clienteEncontrado);
        }
    }, [id]);

    const handleAtualizar = () => {
        alert(`Cliente ${id} atualizado: ${cliente.nome}, ${cliente.numero}`);
        // Aqui você faria uma requisição PUT para o backend
    };

    const handleDeletar = () => {
        alert(`Cliente ${id} deletado`);
        // Aqui você faria uma requisição DELETE para o backend
        navigate('/'); // Volta para a lista de clientes
    };

    return (
        <div>
            <h1>Detalhes do Cliente</h1>
            <form>
                <label>
                    Nome:
                    <input
                        type="text"
                        value={cliente.nome}
                        onChange={(e) => setCliente({ ...cliente, nome: e.target.value })}
                    />
                </label>
                <br />
                <label>
                    Número:
                    <input
                        type="text"
                        value={cliente.numero}
                        onChange={(e) => setCliente({ ...cliente, numero: e.target.value })}
                    />
                </label>
                <br />
                <button type="button" onClick={handleAtualizar}>Atualizar</button>
                <button type="button" onClick={handleDeletar}>Deletar</button>
                <button type="button" onClick={() => navigate('/')}>Voltar</button>
            </form>
        </div>
    );
}

export default DetalhesCliente;
