import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function TodosOrcamentos() {
  const [orcamentos, setOrcamentos] = useState([]);
  const [clientes, setClientes] = useState({}); // Armazena os dados dos clientes
  const navigate = useNavigate();

  // Carregar todos os orçamentos e clientes
  useEffect(() => {
    carregarOrcamentos();
    carregarClientes();
  }, []);

  // Função para carregar os orçamentos
  const carregarOrcamentos = async () => {
    try {
      const response = await fetch('http://localhost:3000/orcamentos');
      if (!response.ok) throw new Error('Erro ao carregar orçamentos');
      const data = await response.json();
      setOrcamentos(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar orçamentos');
    }
  };

  // Função para carregar os clientes
  const carregarClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      if (!response.ok) throw new Error('Erro ao carregar clientes');
      const data = await response.json();
      // Transforma a lista de clientes em um objeto onde a chave é o ID do cliente
      const clientesMap = data.reduce((map, cliente) => {
        map[cliente.id] = cliente;
        return map;
      }, {});
      setClientes(clientesMap);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar clientes');
    }
  };

  // Função para redirecionar para os detalhes do orçamento
  const verDetalhesOrcamento = (id) => {
    navigate(`/detalhes-orcamento/${id}`);
  };

  return (
    <div>
      <h1>Todos os Orçamentos</h1>
      <button onClick={() => navigate('/criar-orcamento')}>Criar Orçamento</button>
      {orcamentos.length > 0 ? (
        orcamentos.map(orcamento => {
          const cliente = clientes[orcamento.clienteId]; // Busca os dados do cliente
          return (
            <div
              key={orcamento.id}
              onClick={() => verDetalhesOrcamento(orcamento.id)}
              style={{ cursor: 'pointer', margin: '10px', padding: '10px', border: '1px solid #ccc' }}
            >
              <h3>Orçamento #{orcamento.id}</h3>
              {cliente ? (
                <>
                  <p><strong>Cliente:</strong> {cliente.nome}</p>
                  <p><strong>Número:</strong> {cliente.numero}</p>
                </>
              ) : (
                <p>Cliente não encontrado</p>
              )}
            </div>
          );
        })
      ) : (
        <p>Nenhum orçamento encontrado.</p>
      )}
    </div>
  );
}

export default TodosOrcamentos;
