import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function CPEditarCliente() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);

  // Carrega os dados do cliente
  useEffect(() => {
    const carregarCliente = async () => {
      try {
        const response = await fetch(`http://localhost:3000/clientes/${id}`);
        if (!response.ok) throw new Error('Erro ao carregar cliente');
        const data = await response.json();
        setCliente(data);
      } catch (error) {
        console.error('Erro:', error);
        alert('Erro ao carregar cliente');
      }
    };

    carregarCliente();
  }, [id]);

  // Atualiza os dados do cliente
  const handleChange = (e) => {
    setCliente({ ...cliente, [e.target.name]: e.target.value });
  };

  // Salva as alterações do cliente
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(cliente),
      });
      if (!response.ok) throw new Error('Erro ao atualizar cliente');
      alert('Cliente atualizado com sucesso!');
      navigate(`/clientes/${id}`);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao atualizar cliente');
    }
  };

  // Deleta o cliente
  const handleDelete = async () => {
    const confirmacao = window.confirm('Tem certeza que deseja apagar este cliente?');
    if (confirmacao) {
      try {
        const response = await fetch(`http://localhost:3000/clientes/${id}`, {
          method: 'DELETE',
        });
        if (!response.ok) throw new Error('Erro ao deletar cliente');
        alert('Cliente apagado com sucesso!');
        navigate('/clientes'); // Redireciona para a lista de clientes
      } catch (error) {
        console.error('Erro:', error);
        alert('Erro ao apagar cliente');
      }
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Editar Cliente</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="nome" value={cliente.nome} onChange={handleChange} placeholder="Nome" required />
        <input type="text" name="numero" value={cliente.numero} onChange={handleChange} placeholder="Número" required />
        <input type="text" name="tecnico" value={cliente.tecnico} onChange={handleChange} placeholder="Técnico" required />
        <input type="text" name="modeloVeiculo" value={cliente.modeloVeiculo} onChange={handleChange} placeholder="Modelo do Veículo" required />
        <input type="text" name="disjuntorEntrada" value={cliente.disjuntorEntrada} onChange={handleChange} placeholder="Disjuntor de Entrada" required />
        <input type="text" name="caboEntrada" value={cliente.caboEntrada} onChange={handleChange} placeholder="Cabo de Entrada" required />
        <button type="submit">Salvar</button>
        <button type="button" onClick={() => navigate(`/clientes/${id}`)}>Cancelar</button>
        <button type="button" onClick={handleDelete} style={{ backgroundColor: 'red', color: 'white' }}>Apagar</button>
      </form>
    </div>
  );
}

export default CPEditarCliente;
