import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function DetalhesCliente() {
  const { id } = useParams(); // Captura o ID da URL
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);
  const [orcamentos, setOrcamentos] = useState([]);

  // Carregar detalhes do cliente e orçamentos
  useEffect(() => {
    carregarCliente();
    carregarOrcamentos();
  }, [id]);

  // Função para carregar os detalhes do cliente
  const carregarCliente = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`);
      if (!response.ok) throw new Error('Erro ao carregar cliente');
      const data = await response.json();
      setCliente(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar cliente');
    }
  };

  // Função para carregar os orçamentos do cliente
  const carregarOrcamentos = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}/orcamentos`);
      if (!response.ok) throw new Error('Erro ao carregar orçamentos');
      const data = await response.json();
      setOrcamentos(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar orçamentos');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Número:</strong> {cliente.numero}</p>
      <p><strong>Técnico:</strong> {cliente.tecnico}</p>
      <p><strong>Modelo do Veículo:</strong> {cliente.modelo_veiculo}</p>
      <p><strong>Disjuntor de Entrada:</strong> {cliente.disjuntor_entrada}</p>
      <p><strong>Cabo de Entrada:</strong> {cliente.cabo_entrada}</p>

      <button onClick={() => navigate(`/editar/${id}`)}>Editar</button>
      <button onClick={() => navigate('/')}>Voltar</button>

      {/* Lista de Orçamentos do Cliente */}
      <h2>Orçamentos</h2>
      {orcamentos.length > 0 ? (
        orcamentos.map(orcamento => (
          <div key={orcamento.id}>
            <h3>Orçamento #{orcamento.id}</h3>
            <pre>{orcamento.resultado}</pre>
          </div>
        ))
      ) : (
        <p>Nenhum orçamento encontrado.</p>
      )}
    </div>
  );
}

export default DetalhesCliente;
