import React, { useState } from 'react';

function FormularioCliente({ onAdicionarCliente }) {
  const [nome, setNome] = useState('');
  const [numero, setNumero] = useState('');
  const [tecnico, setTecnico] = useState('');
  const [modeloVeiculo, setModeloVeiculo] = useState('');
  const [disjuntorEntrada, setDisjuntorEntrada] = useState('');
  const [caboEntrada, setCaboEntrada] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const cliente = { nome, numero, tecnico, modeloVeiculo, disjuntorEntrada, caboEntrada };
    onAdicionarCliente(cliente); // Chama a função passada como prop
  };

  return (
    <div>
      <h2>Adicionar Cliente</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} placeholder="Nome" required />
        <input type="text" value={numero} onChange={(e) => setNumero(e.target.value)} placeholder="Número" required />
        <input type="text" value={tecnico} onChange={(e) => setTecnico(e.target.value)} placeholder="Técnico" required />
        <input type="text" value={modeloVeiculo} onChange={(e) => setModeloVeiculo(e.target.value)} placeholder="Modelo do Veículo" required />
        <input type="text" value={disjuntorEntrada} onChange={(e) => setDisjuntorEntrada(e.target.value)} placeholder="Disjuntor de Entrada" required />
        <input type="text" value={caboEntrada} onChange={(e) => setCaboEntrada(e.target.value)} placeholder="Cabo de Entrada" required />
        <button type="submit">Adicionar</button>
      </form>
    </div>
  );
}

export default FormularioCliente;
