import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ListaClientes() {
  const [clientes, setClientes] = useState([]);
  const [termoBusca, setTermoBusca] = useState('');
  const navigate = useNavigate();

  // Carregar clientes ao iniciar
  useEffect(() => {
    carregarClientes();
  }, []);

  const carregarClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      if (!response.ok) throw new Error('Erro ao carregar clientes');
      const data = await response.json();
      setClientes(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar clientes');
    }
  };

  // Filtrar clientes com base no termo de busca
  const clientesFiltrados = clientes.filter(cliente => {
    const nomeMatch = cliente.nome.toLowerCase().includes(termoBusca.toLowerCase());
    const numeroMatch = cliente.numero.toLowerCase().includes(termoBusca.toLowerCase());
    return nomeMatch || numeroMatch;
  });

  return (
    <div>
      <h1>Clientes Cadastrados</h1>
      <div>
        <input
          type="text"
          placeholder="Buscar por nome ou número"
          value={termoBusca}
          onChange={(e) => setTermoBusca(e.target.value)}
        />
      </div>
      <button onClick={() => navigate('/adicionar')}>Adicionar Cliente</button>
      <ul>
        {clientesFiltrados.map(cliente => (
          <li key={cliente.id}>
            <strong>Nome:</strong> {cliente.nome}<br />
            <strong>Número:</strong> {cliente.numero}<br />
            <button onClick={() => navigate(`/detalhes/${cliente.id}`)}>Ver Detalhes</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ListaClientes;
