import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function CPEditarCliente() {
  const { idCliente } = useParams(); // Alterado de 'id' para 'idCliente'
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);

  useEffect(() => {
    const carregarCliente = async () => {
      try {
        const response = await fetch(`http://localhost:3000/clientes/${idCliente}`); // Alterado para 'idCliente'
        if (!response.ok) throw new Error('Erro ao carregar cliente');
        const data = await response.json();
        setCliente(data);
      } catch (error) {
        console.error('Erro:', error);
        alert('Erro ao carregar cliente');
      }
    };

    carregarCliente();
  }, [idCliente]);

  const handleChange = (e) => {
    setCliente({ ...cliente, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3000/clientes/${idCliente}`, { // Alterado para 'idCliente'
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(cliente),
      });
      if (!response.ok) throw new Error('Erro ao atualizar cliente');
      alert('Cliente atualizado com sucesso!');
      navigate(`/clientes/${idCliente}`); // Alterado para 'idCliente'
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao atualizar cliente');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Editar Cliente</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="nomeCliente" // Alterado para 'nomeCliente'
          value={cliente.nomeCliente} // Alterado para 'nomeCliente'
          onChange={handleChange}
          placeholder="Nome"
          required
        />
        <input
          type="text"
          name="codSankhya" // Alterado para 'codSankhya'
          value={cliente.codSankhya} // Alterado para 'codSankhya'
          onChange={handleChange}
          placeholder="Código Sankhya"
          required
        />
        <input
          type="text"
          name="telefone" // Alterado para 'telefone'
          value={cliente.telefone} // Alterado para 'telefone'
          onChange={handleChange}
          placeholder="Telefone"
          required
        />
        <input
          type="text"
          name="endereco" // Alterado para 'endereco'
          value={cliente.endereco} // Alterado para 'endereco'
          onChange={handleChange}
          placeholder="Endereço"
          required
        />
        <input
          type="text"
          name="cep" // Alterado para 'cep'
          value={cliente.cep} // Alterado para 'cep'
          onChange={handleChange}
          placeholder="CEP"
          required
        />
        <input
          type="text"
          name="veiculo" // Alterado para 'veiculo'
          value={cliente.veiculo} // Alterado para 'veiculo'
          onChange={handleChange}
          placeholder="Veículo"
          required
        />
        <button type="submit">Salvar</button>
      </form>
      <button onClick={() => navigate(`/clientes/detalhes/${idCliente}`)}>Cancelar</button>
    </div>
  );
}

export default CPEditarCliente;
