import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ClienteDetalhes from './ClienteDetalhes'; // Componente reutilizável
import OrcamentoLista from './OrcamentoLista'; // Componente reutilizável

function DetalhesCliente() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);
  const [orcamentos, setOrcamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    Promise.all([carregarCliente(), carregarOrcamentos()])
      .then(() => setLoading(false))
      .catch((error) => {
        console.error('Erro:', error);
        setError('Erro ao carregar dados');
        setLoading(false);
      });
  }, [id]);

  const carregarCliente = async () => {
    const response = await fetch(`http://localhost:3000/clientes/${id}`);
    if (!response.ok) throw new Error('Erro ao carregar cliente');
    const data = await response.json();
    setCliente(data);
  };

  const carregarOrcamentos = async () => {
    const response = await fetch(`http://localhost:3000/clientes/${id}/orcamentos`);
    if (!response.ok) throw new Error('Erro ao carregar orçamentos');
    const data = await response.json();
    setOrcamentos(data);
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <ClienteDetalhes cliente={cliente} />
      <button onClick={() => navigate(`/editar/${id}`)}>Editar</button>
      <button onClick={() => navigate('/')}>Voltar</button>

      <h2>Orçamentos</h2>
      <OrcamentoLista orcamentos={orcamentos} />
    </div>
  );
}

export default DetalhesCliente;
