import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Orcamento from './Orcamento';

function DetalhesCliente() {
  const { id } = useParams();
  const [cliente, setCliente] = useState(null);
  const [orcamentos, setOrcamentos] = useState([]);
  const [mostrarOrcamento, setMostrarOrcamento] = useState(false);

  // Carregar detalhes do cliente e orçamentos
  useEffect(() => {
    carregarCliente();
    carregarOrcamentos();
  }, [id]);

  const carregarCliente = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`);
      if (!response.ok) throw new Error('Erro ao carregar cliente');
      const data = await response.json();
      setCliente(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar cliente');
    }
  };

  const carregarOrcamentos = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}/orcamentos`);
      if (!response.ok) throw new Error('Erro ao carregar orçamentos');
      const data = await response.json();
      setOrcamentos(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar orçamentos');
    }
  };

  const handleSalvarOrcamento = async (orcamento) => {
    try {
      const response = await fetch('http://localhost:3000/orcamentos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...orcamento, clienteId: id })
      });
      if (!response.ok) throw new Error('Erro ao salvar orçamento');
      carregarOrcamentos(); // Recarrega os orçamentos após salvar
      setMostrarOrcamento(false); // Fecha o formulário de orçamento
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao salvar orçamento');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Número:</strong> {cliente.numero}</p>
      <button onClick={() => setMostrarOrcamento(!mostrarOrcamento)}>
        {mostrarOrcamento ? 'Fechar Orçamento' : 'Criar Orçamento'}
      </button>

      {mostrarOrcamento && (
        <Orcamento clienteId={id} onSalvarOrcamento={handleSalvarOrcamento} />
      )}

      <h2>Orçamentos</h2>
      {orcamentos.map(orcamento => (
        <div key={orcamento.id}>
          <pre>{orcamento.resultado}</pre>
        </div>
      ))}
    </div>
  );
}

export default DetalhesCliente;
