import React, { useState } from 'react';

function CPBuscaCep({ onCepChange, onEnderecoChange }) {
  const [cep, setCep] = useState('');

  const handleBuscarCep = async () => {
    if (cep.length !== 8) {
      alert('CEP inválido. O CEP deve ter 8 dígitos.');
      return;
    }

    try {
      const response = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
      const data = await response.json();

      if (data.erro) {
        alert('CEP não encontrado.');
        return;
      }

      // Passa os dados do endereço para o componente pai
      onEnderecoChange({
        endereco: data.logradouro,
        cep: data.cep,
      });
    } catch (error) {
      console.error('Erro ao buscar CEP:', error);
      alert('Erro ao buscar CEP. Tente novamente.');
    }
  };

  return (
    <div>
      <input
        type="text"
        value={cep}
        onChange={(e) => {
          setCep(e.target.value.replace(/\D/g, '')); // Remove caracteres não numéricos
          onCepChange(e.target.value.replace(/\D/g, '')); // Passa o CEP para o componente pai
        }}
        placeholder="Digite o CEP"
        maxLength={8}
      />
      <button type="button" onClick={handleBuscarCep}>
        Buscar CEP
      </button>
    </div>
  );
}

export default CPBuscaCep;
