import React from 'react';
import FormularioCliente from '../components/cliente/FormularioCliente'; // Ajuste o caminho conforme necess치rio
import { useNavigate } from 'react-router-dom';

function CadastroClientes() {
  const navigate = useNavigate();

  const handleAdicionarCliente = async (cliente) => {
    try {
      const response = await fetch('http://localhost:3000/clientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(cliente),
      });

      if (!response.ok) throw new Error('Erro ao cadastrar cliente');
      alert('Cliente cadastrado com sucesso!');
      navigate('/clientes'); // Redireciona para a lista de clientes ap칩s o cadastro
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao cadastrar cliente');
    }
  };

  return (
    <div>
      <h1>Cadastro de Clientes</h1>
      <FormularioCliente onAdicionarCliente={handleAdicionarCliente} />
      <button onClick={() => navigate('/clientes')}>Voltar para Lista</button>
    </div>
  );
}

export default CadastroClientes;
