import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import OrcamentoLista from '../components/orcamento/OrcamentoLista'; // Componente de lista de orçamentos
import ClienteDetalhes from '../components/cliente/ClienteDetalhes'; // Componente de detalhes do cliente

function DetalhesClientes() {
  const { id } = useParams(); // Captura o ID do cliente da URL
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);
  const [orcamentos, setOrcamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Carrega os dados do cliente e os orçamentos associados
  useEffect(() => {
    const carregarDados = async () => {
      try {
        const [clienteData, orcamentosData] = await Promise.all([carregarCliente(), carregarOrcamentos()]);
        setCliente(clienteData);
        setOrcamentos(orcamentosData);
        setLoading(false);
      } catch (error) {
        console.error('Erro:', error);
        setError('Erro ao carregar dados');
        setLoading(false);
      }
    };

    carregarDados();
  }, [id]);

  // Função para carregar os detalhes do cliente
  const carregarCliente = async () => {
    const response = await fetch(`http://localhost:3000/clientes/${id}`);
    if (!response.ok) throw new Error('Erro ao carregar cliente');
    return await response.json();
  };

  // Função para carregar os orçamentos do cliente
  const carregarOrcamentos = async () => {
    const response = await fetch(`http://localhost:3000/clientes/${id}/orcamentos`);
    if (!response.ok) throw new Error('Erro ao carregar orçamentos');
    return await response.json();
  };

  // Exibe um loading enquanto os dados são carregados
  if (loading) return <div>Carregando...</div>;

  // Exibe uma mensagem de erro caso ocorra algum problema
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>

      {/* Exibe os detalhes do cliente usando o componente ClienteDetalhes */}
      <ClienteDetalhes cliente={cliente} />

      {/* Botões para editar o cliente e voltar para a lista de clientes */}
      <button onClick={() => navigate(`/clientes/editar/${id}`)}>Editar Cliente</button>
      <button onClick={() => navigate('/clientes')}>Voltar para Lista</button>

      {/* Exibe a lista de orçamentos do cliente */}
      <h2>Orçamentos</h2>
      <OrcamentoLista orcamentos={orcamentos} />
    </div>
  );
}

export default DetalhesClientes;
