import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function DetalhesCliente() {
  const { id } = useParams(); // Captura o ID da URL
  const [cliente, setCliente] = useState(null);

  // Carregar detalhes do cliente
  useEffect(() => {
    carregarCliente();
  }, [id]);

  const carregarCliente = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`);
      if (!response.ok) throw new Error('Erro ao carregar cliente');
      const data = await response.json();
      setCliente(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar cliente');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Número:</strong> {cliente.numero}</p>
      <p><strong>Técnico:</strong> {cliente.tecnico}</p>
      <p><strong>Modelo do Veículo:</strong> {cliente.modelo_veiculo}</p>
      <p><strong>Disjuntor de Entrada:</strong> {cliente.disjuntor_entrada}</p>
      <p><strong>Cabo de Entrada:</strong> {cliente.cabo_entrada}</p>
      <button onClick={() => window.history.back()}>Voltar</button>
    </div>
  );
}

export default DetalhesCliente;
