import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function DetalhesCliente() {
  const { id } = useParams(); // Captura o ID da URL
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);
  const [orcamentos, setOrcamentos] = useState([]);
  const [mostrarOrcamento, setMostrarOrcamento] = useState(false);

  // Carregar detalhes do cliente e orçamentos
  useEffect(() => {
    carregarCliente();
    carregarOrcamentos();
  }, [id]);

  // Função para carregar os detalhes do cliente
  const carregarCliente = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`);
      if (!response.ok) throw new Error('Erro ao carregar cliente');
      const data = await response.json();
      setCliente(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar cliente');
    }
  };

  // Função para carregar os orçamentos do cliente
  const carregarOrcamentos = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}/orcamentos`);
      if (!response.ok) throw new Error('Erro ao carregar orçamentos');
      const data = await response.json();
      setOrcamentos(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar orçamentos');
    }
  };

  // Função para excluir o cliente
  const handleExcluir = async () => {
    if (!window.confirm('Tem certeza que deseja excluir este cliente?')) return;
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Erro ao excluir cliente');
      alert('Cliente excluído com sucesso!');
      navigate('/'); // Redireciona para a lista de clientes
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao excluir cliente');
    }
  };

  // Função para salvar um orçamento
  const handleSalvarOrcamento = async (orcamento) => {
    try {
      const response = await fetch('http://localhost:3000/orcamentos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...orcamento, clienteId: id })
      });
      if (!response.ok) throw new Error('Erro ao salvar orçamento');
      carregarOrcamentos(); // Recarrega os orçamentos após salvar
      setMostrarOrcamento(false); // Fecha o formulário de orçamento
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao salvar orçamento');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Número:</strong> {cliente.numero}</p>
      <p><strong>Técnico:</strong> {cliente.tecnico}</p>
      <p><strong>Modelo do Veículo:</strong> {cliente.modelo_veiculo}</p>
      <p><strong>Disjuntor de Entrada:</strong> {cliente.disjuntor_entrada}</p>
      <p><strong>Cabo de Entrada:</strong> {cliente.cabo_entrada}</p>

      <button onClick={() => navigate(`/editar/${id}`)}>Editar</button>
      <button onClick={handleExcluir}>Excluir</button>
      <button onClick={() => setMostrarOrcamento(!mostrarOrcamento)}>
        {mostrarOrcamento ? 'Fechar Orçamento' : 'Criar Orçamento'}
      </button>
      <button onClick={() => navigate('/')}>Voltar</button>

      {/* Formulário de Orçamento */}
      {mostrarOrcamento && (
        <div>
          <h2>Criar Orçamento</h2>
          <form onSubmit={(e) => {
            e.preventDefault();
            const formData = new FormData(e.target);
            const orcamento = {
              distancia: parseFloat(formData.get('distancia')),
              quantidadeEletrocalha: parseFloat(formData.get('quantidadeEletrocalha')),
              quantidadeDutoCanaduto: parseFloat(formData.get('quantidadeDutoCanaduto')),
              quantidadeEletrodutoPVC: parseFloat(formData.get('quantidadeEletrodutoPVC')),
              desviosCanaduto: parseInt(formData.get('desviosCanaduto')),
              desviosPVC: parseInt(formData.get('desviosPVC')),
              conduletes: parseInt(formData.get('conduletes')),
              quantidadeCurvas: parseInt(formData.get('quantidadeCurvas')),
              observacao: formData.get('observacao'),
              resultado: '' // O resultado será calculado no backend ou no frontend
            };
            handleSalvarOrcamento(orcamento);
          }}>
            <div>
              <label>Distância (m):</label>
              <input type="number" name="distancia" required />
            </div>
            <div>
              <label>Quantidade de Eletrocalha (m):</label>
              <input type="number" name="quantidadeEletrocalha" required />
            </div>
            <div>
              <label>Quantidade de Duto Canaduto (m):</label>
              <input type="number" name="quantidadeDutoCanaduto" required />
            </div>
            <div>
              <label>Quantidade de Eletroduto PVC (m):</label>
              <input type="number" name="quantidadeEletrodutoPVC" required />
            </div>
            <div>
              <label>Desvios Canaduto:</label>
              <input type="number" name="desviosCanaduto" required />
            </div>
            <div>
              <label>Desvios PVC:</label>
              <input type="number" name="desviosPVC" required />
            </div>
            <div>
              <label>Conduletes:</label>
              <input type="number" name="conduletes" required />
            </div>
            <div>
              <label>Quantidade de Curvas:</label>
              <input type="number" name="quantidadeCurvas" required />
            </div>
            <div>
              <label>Observação:</label>
              <textarea name="observacao" required />
            </div>
            <button type="submit">Salvar Orçamento</button>
          </form>
        </div>
      )}

      {/* Lista de Orçamentos */}
      <h2>Orçamentos</h2>
      {orcamentos.length > 0 ? (
        orcamentos.map(orcamento => (
          <div key={orcamento.id}>
            <pre>{orcamento.resultado}</pre>
          </div>
        ))
      ) : (
        <p>Nenhum orçamento encontrado.</p>
      )}
    </div>
  );
}

export default DetalhesCliente;
