import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ListaClientes from '../components/cliente/ListaClientes';

function Clientes() {
  const navigate = useNavigate();
  const [clientes, setClientes] = useState([]);
  const [termoBusca, setTermoBusca] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    carregarClientes();
  }, []);

  const carregarClientes = async () => {
    try {
      const response = await fetch('http://localhost:3000/clientes');
      if (!response.ok) throw new Error('Erro ao carregar clientes');
      const data = await response.json();
      console.log('Dados carregados:', data); // Verifique os dados aqui
      if (!Array.isArray(data)) throw new Error('Dados inválidos recebidos da API');
      setClientes(data);
    } catch (error) {
      console.error('Erro:', error);
      setError(error.message); // Exibe a mensagem de erro específica
    } finally {
      setLoading(false);
    }
  };

  const clientesFiltrados = clientes.filter((cliente) =>
    cliente.nome.toLowerCase().includes(termoBusca.toLowerCase()) ||
    cliente.numero.toLowerCase().includes(termoBusca.toLowerCase())
  );

  if (loading) return <p>Carregando...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Clientes Cadastrados</h1>
      <input
        type="text"
        placeholder="Buscar por nome ou número"
        value={termoBusca}
        onChange={(e) => setTermoBusca(e.target.value)}
      />
      <button onClick={() => navigate('/cadastro-clientes')}>Cadastrar Novo Cliente</button>
      {Array.isArray(clientesFiltrados) && clientesFiltrados.length > 0 ? (
        <ListaClientes clientes={clientesFiltrados} navigate={navigate} />
      ) : (
        <p>Nenhum cliente encontrado.</p>
      )}
    </div>
  );
}

export default Clientes;
