import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ClienteDetalhes from '../cliente/ClienteDetalhes'; // Caminho corrigido
import OrcamentoDetalhes from '../orcamento/OrcamentoDetalhes'; // Caminho corrigido

function DetalhesOrcamento() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [orcamento, setOrcamento] = useState(null);
  const [cliente, setCliente] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    carregarOrcamento()
      .then(() => setLoading(false))
      .catch((error) => {
        console.error('Erro:', error);
        setError('Erro ao carregar dados');
        setLoading(false);
      });
  }, [id]);

  const carregarOrcamento = async () => {
    const responseOrcamento = await fetch(`http://localhost:3000/orcamentos/${id}`);
    if (!responseOrcamento.ok) throw new Error('Erro ao carregar orçamento');
    const dataOrcamento = await responseOrcamento.json();
    setOrcamento(dataOrcamento);

    const responseCliente = await fetch(`http://localhost:3000/clientes/${dataOrcamento.clienteId}`);
    if (!responseCliente.ok) throw new Error('Erro ao carregar cliente');
    const dataCliente = await responseCliente.json();
    setCliente(dataCliente);
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Detalhes do Orçamento #{orcamento.id}</h1>
      <button onClick={() => navigate('/orcamentos')}>Voltar</button>

      {cliente && <ClienteDetalhes cliente={cliente} />}
      {orcamento && <OrcamentoDetalhes orcamento={orcamento} />}
    </div>
  );
}

export default DetalhesOrcamento;
