import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function DetalhesCliente() {
  const { id } = useParams(); // Captura o ID da URL
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);

  // Carregar detalhes do cliente
  useEffect(() => {
    const carregarCliente = async () => {
      try {
        const response = await fetch(`http://localhost:3000/clientes/${id}`);
        if (!response.ok) throw new Error('Erro ao carregar cliente');
        const data = await response.json();
        setCliente(data);
      } catch (error) {
        console.error('Erro:', error);
        alert('Erro ao carregar cliente');
      }
    };
    carregarCliente();
  }, [id]);

  // Função para excluir o cliente
  const handleExcluir = async () => {
    if (!window.confirm('Tem certeza que deseja excluir este cliente?')) return;
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Erro ao excluir cliente');
      alert('Cliente excluído com sucesso!');
      navigate('/'); // Redireciona para a lista de clientes
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao excluir cliente');
    }
  };

  if (!cliente) return <div>Carregando...</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <p><strong>Nome:</strong> {cliente.nome}</p>
      <p><strong>Número:</strong> {cliente.numero}</p>
      <p><strong>Técnico:</strong> {cliente.tecnico}</p>
      <p><strong>Modelo do Veículo:</strong> {cliente.modelo_veiculo}</p>
      <p><strong>Disjuntor de Entrada:</strong> {cliente.disjuntor_entrada}</p>
      <p><strong>Cabo de Entrada:</strong> {cliente.cabo_entrada}</p>

      <button onClick={() => navigate(`/editar/${id}`)}>Editar</button>
      <button onClick={handleExcluir}>Excluir</button>
      <button onClick={() => navigate(`/criar-orcamento`)}>Criar Orçamento</button>
      <button onClick={() => navigate(`/clientes/${id}/orcamentos`)}>Ver Orçamentos</button>
      <button onClick={() => navigate('/')}>Voltar</button>
    </div>
  );
}

export default DetalhesCliente;
