import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditarCliente() {
  const { id } = useParams(); // Captura o ID da URL
  const navigate = useNavigate();
  const [cliente, setCliente] = useState({
    nome: '',
    numero: '',
    tecnico: '',
    modelo_veiculo: '',
    disjuntor_entrada: '',
    cabo_entrada: ''
  });

  // Carregar cliente para edição
  useEffect(() => {
    carregarCliente();
  }, [id]);

  const carregarCliente = async () => {
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`);
      if (!response.ok) throw new Error('Erro ao carregar cliente');
      const data = await response.json();
      setCliente(data);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao carregar cliente');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3000/clientes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(cliente)
      });
      if (!response.ok) throw new Error('Erro ao atualizar cliente');
      alert('Cliente atualizado com sucesso!');
      navigate(`/detalhes/${id}`);
    } catch (error) {
      console.error('Erro:', error);
      alert('Erro ao atualizar cliente');
    }
  };

  return (
    <div>
      <h1>Editar Cliente</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={cliente.nome} onChange={(e) => setCliente({ ...cliente, nome: e.target.value })} placeholder="Nome" required />
        <input type="text" value={cliente.numero} onChange={(e) => setCliente({ ...cliente, numero: e.target.value })} placeholder="Número" required />
        <input type="text" value={cliente.tecnico} onChange={(e) => setCliente({ ...cliente, tecnico: e.target.value })} placeholder="Técnico" required />
        <input type="text" value={cliente.modelo_veiculo} onChange={(e) => setCliente({ ...cliente, modelo_veiculo: e.target.value })} placeholder="Modelo do Veículo" required />
        <input type="text" value={cliente.disjuntor_entrada} onChange={(e) => setCliente({ ...cliente, disjuntor_entrada: e.target.value })} placeholder="Disjuntor de Entrada" required />
        <input type="text" value={cliente.cabo_entrada} onChange={(e) => setCliente({ ...cliente, cabo_entrada: e.target.value })} placeholder="Cabo de Entrada" required />
        <button type="submit">Salvar</button>
        <button type="button" onClick={() => navigate(`/detalhes/${id}`)}>Cancelar</button>
      </form>
    </div>
  );
}

export default EditarCliente;
