import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ClienteDetalhes from '../components/cliente/CPClienteDetalhes';

function DetalhesClientes() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [cliente, setCliente] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const carregarCliente = async () => {
      try {
        const response = await fetch(`http://localhost:3000/clientes/${id}`);
        if (!response.ok) throw new Error('Erro ao carregar cliente');
        const data = await response.json();
        setCliente(data);
      } catch (error) {
        setError('Erro ao carregar dados do cliente');
      } finally {
        setLoading(false);
      }
    };

    carregarCliente();
  }, [id]);

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>{error}</div>;
  if (!cliente) return <div>Cliente n√£o encontrado.</div>;

  return (
    <div>
      <h1>Detalhes do Cliente</h1>
      <ClienteDetalhes cliente={cliente} />
      <button onClick={() => navigate(`/clientes/detalhes/editar/${id}`)}>Editar Cliente</button>
      <button onClick={() => navigate('/clientes')}>Voltar para Lista</button>
    </div>
  );
}

export default DetalhesClientes;
